// Basic PageView tracking for Adobe Analytics
// This rule fires on every page load to send page view data

// Set page name from data layer or URL
var pageName = _satellite.getVar('pageName') || window.location.pathname;
var pageURL = window.location.href;

// Send page view beacon
_satellite.track('pageview', {
  pageName: pageName,
  pageURL: pageURL,
  timestamp: new Date().toISOString()
});
