// Form submission tracking for Adobe Launch
// Tracks form submissions with validation and error handling

(function() {
  var form = event.target.closest('form');
  if (!form) return;

  var formId = form.id || form.getAttribute('name') || 'unnamed-form';
  var formName = form.getAttribute('data-form-name') || formId;
  var formAction = form.action || 'no-action';
  var formMethod = form.method || 'get';

  // Get form fields (optional - be mindful of PII)
  var fieldCount = form.querySelectorAll('input, select, textarea').length;
  var requiredFields = form.querySelectorAll('[required]').length;

  // Check if form is valid (HTML5 validation)
  var isValid = form.checkValidity();

  // Send form submission event
  _satellite.track('formSubmit', {
    formId: formId,
    formName: formName,
    formAction: formAction,
    formMethod: formMethod,
    fieldCount: fieldCount,
    requiredFields: requiredFields,
    isValid: isValid,
    timestamp: new Date().toISOString()
  });
})();
