// Link click tracking for Adobe Launch
// Tracks all outbound and download links

(function() {
  // Get the clicked link element
  var linkElement = event.target.closest('a');
  if (!linkElement) return;

  var linkURL = linkElement.href;
  var linkText = linkElement.textContent.trim() || linkElement.getAttribute('aria-label') || 'No text';
  var isOutbound = linkElement.hostname !== window.location.hostname;
  var isDownload = linkElement.hasAttribute('download') || /\.(pdf|zip|doc|docx|xls|xlsx)$/i.test(linkURL);

  // Determine link type
  var linkType = 'other';
  if (isOutbound) linkType = 'outbound';
  else if (isDownload) linkType = 'download';
  else linkType = 'internal';

  // Send link click event
  _satellite.track('linkClick', {
    linkURL: linkURL,
    linkText: linkText,
    linkType: linkType,
    isOutbound: isOutbound,
    isDownload: isDownload
  });
})();
